import React, { useState, useEffect } from 'react';
import { usePlayer } from '../PlayerContext'; 
import { useNavigate } from 'react-router-dom';

const Settings = () => {
  const { settings, updateSettings } = usePlayer();
  const [localSettings, setLocalSettings] = useState(settings);
  const navigate = useNavigate();

  useEffect(() => {
    setLocalSettings(settings);
  }, [settings]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLocalSettings({ ...localSettings, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    updateSettings(localSettings);
    navigate('/menu');
  };

  const goHome = () => {
    navigate('/menu'); // Navigate to the home route (adjust the route as needed)
  };

  return (
    <div className="settings-container">
      <h2 className="settings-title">Game Settings</h2>
      <form onSubmit={handleSubmit} className="settings-form">
        <div className="form-group">
          <label className="form-label">Snake Color:</label>
          <input
            type="text"
            name="snakeColor"
            className="form-input"
            value={localSettings.snakeColor || ''}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label className="form-label">Food Color:</label>
          <input
            type="text"
            name="foodColor"
            className="form-input"
            value={localSettings.foodColor || ''}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label className="form-label">Background Color:</label>
          <input
            type="text"
            name="backgroundColor"
            className="form-input"
            value={localSettings.backgroundColor || ''}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <label className="form-label">Snake Speed:</label>
          <input
            type="number"
            name="snakeSpeed"
            className="form-input"
            value={localSettings.snakeSpeed || ''}
            onChange={handleChange}
          />
        </div>
        <div className="form-group">
          <button type="submit" className="submit-button">Save Settings</button>
          <button type="button" onClick={goHome} className="go-home-button">Go Home</button>
        </div>
      </form>
    </div>
  );
};

export default Settings;
