import React, { useState } from "react";
import Menu from "./components/Menu";
import EnterCredentials from "./components/EnterCredentials";
import Game from "./components/Game";
import Settings from "./components/Settings";
import Leaderboard from "./components/Leaderboard";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { PlayerProvider } from './PlayerContext';

function App() {

  const [playerId, setPlayerId] = useState('');

  return (
    <Router>
      <PlayerProvider>
        <Routes>
          <Route path="/" element={<EnterCredentials />} />
          <Route path="/menu" element={<Menu id={playerId}/>} />
          <Route path="/play" element={<Game />} />
          <Route path="/leaderboard" element={<Settings />} />
          <Route path="/settings" element={<Leaderboard />} />
        </Routes>
      </PlayerProvider>
    </Router>
  );
}

export default App;
