import React from 'react';
import { Link } from 'react-router-dom';
import './styles/Menu.css'; 
import { usePlayer } from '../PlayerContext'; 

const Menu = () => {

    const { playerId, setPlayerId } = usePlayer(); 

    const handleLogout = () => {
        setPlayerId(null);
    }

    return (
        <div className="menu">
            <h1 className="menu-title">Welcome player {playerId}</h1>
            <Link to="/play" className="menu-link">
                <button className="menu-button" onClick={handleStartGame}>
                    Play
                </button>
            </Link>
            <Link to="/leaderboard" className="menu-link">
                <button className="menu-button">
                    Leaderboard
                </button>
            </Link>
            <Link to="/settings" className="menu-link">
                <button className="menu-button">
                    Settings
                </button>
            </Link>
            <Link to="/" className="menu-link">
                <button onClick={handleLogout} className="menu-button">
                    Logout
                </button>
            </Link>

        </div>
    );
}

export default Menu;
