import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const Leaderboard = () => {
  const [players, setPlayers] = useState([]);
  const navigate = useNavigate();

  const apiPath = 'http://localhost:3001/players';

  const goHome = () => {
    navigate('/menu'); // Navigate to the home route (adjust the route as needed)
  };

  useEffect(() => {
    const fetchLeaderboardData = async () => {
      try {
        const response = await fetch(apiPath); // Replace with your actual API endpoint
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        const data = await response.json();

        // Sort players by highScore in descending order
        const sortedPlayers = data.sort((a, b) => b.highScore - a.highScore);

        setPlayers(sortedPlayers);
      } catch (error) {
        console.error('There has been a problem with your fetch operation:', error);
      }
    };

    fetchLeaderboardData();
  }, []);

  return (
    <div>
      <h1>Leaderboard</h1>
      <button onClick={goHome} className="back-home">Back Home</button>
      <table>
        <thead>
          <tr>
            <th>Player</th>
            <th>Score</th>
          </tr>
        </thead>
        <tbody>
          {players.map((player, index) => (
            <tr key={index}>
              <td>{player.username}</td>
              <td>{player.highScore}</td> 
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Leaderboard;
