import React, { useState, useEffect } from 'react';
import { usePlayer } from '../PlayerContext'; 
import { Link } from 'react-router-dom';

const Settings = () => {
  const { settings, updateSettings } = usePlayer();
  const [localSettings, setLocalSettings] = useState(settings);

  useEffect(() => {
    setLocalSettings(settings);
  }, [settings]);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setLocalSettings({ ...localSettings, [name]: value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(`Submitting settings: ${JSON.stringify(localSettings)}`);
    updateSettings(localSettings);
  };

  return (
    <div>
      <h2>Game Settings</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Snake Color:</label>
          <input
            type="text"
            name="snakeColor"
            value={localSettings.snakeColor || ''}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Food Color:</label>
          <input
            type="text"
            name="foodColor"
            value={localSettings.foodColor || ''}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Background Color:</label>
          <input
            type="text"
            name="backgroundColor"
            value={localSettings.backgroundColor || ''}
            onChange={handleChange}
          />
        </div>
        <div>
          <label>Snake Speed:</label>
          <input
            type="number"
            name="snakeSpeed"
            value={localSettings.snakeSpeed || ''}
            onChange={handleChange}
          />
        </div>
        <div>
          <Link to="/menu">
            <button type="submit">Save Settings</button>
          </Link>
        </div>
      </form>
    </div>
  );
};

export default Settings;
